var crypto = require('crypto');

exports.fromString = function (str) {
	var algo = crypto.createHash('sha1');
	algo.update(str);
	return algo.digest('hex');
};

exports.random = function () {
	var algo = crypto.createHash('sha1');
	algo.update((new Date()).getTime() + 'oiergnergoingeo90');
	algo.update(Math.random() + 'erugwgoineoig984g');
	return algo.digest('hex');
};